import React, { Component } from 'react';
import './App.css';


class DrumMachine extends Component {
  constructor(props){
    super(props)
    this.state  = {
    }
    this.handlePressQ = this.handlePressQ.bind(this);
  }

  handlePressQ() {

  };

  render(){
    return (
      <div id="drum-machine">
      <button id="snare1" className="drum-pad">Q<audio className="clip" id="Q" src="https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3" autoplay></audio></button>
      <button id="snare2" className="drum-pad"><audio id="W"></audio>W</button>
      <button id="snare3" className="drum-pad"><audio id="E"></audio>E</button>
      <button id="kick1" className="drum-pad"><audio id="A"></audio>A</button>
      <button id="kick2" className="drum-pad"><audio id="S"></audio>S</button>
      <button id="kick3" className="drum-pad"><audio id="D"></audio>D</button>
      <button id="clap1" className="drum-pad"><audio id="Z"></audio>Z</button>
      <button id="clap2" className="drum-pad"><audio id="X"></audio>X</button>
      <button id="clap3" className="drum-pad"><audio id="C"></audio>C</button>
      <div id="display"></div>
    </div>
    )
  }
}

export default DrumMachine; 

