import React, { Component } from 'react';
import './App.css';


class DrumMachine extends Component {
  constructor(props){
    super(props)
    this.state = {
    }
    this.handlePressQ = this.handlePressQ.bind(this);
    this.handlePressW = this.handlePressW.bind(this);
    this.handlePressE = this.handlePressE.bind(this);
    this.handlePressA = this.handlePressA.bind(this);
    this.handlePressS = this.handlePressS.bind(this);
    this.handlePressD = this.handlePressD.bind(this);
    this.handlePressZ = this.handlePressZ.bind(this);
    this.handlePressX = this.handlePressX.bind(this);
    this.handlePressC = this.handlePressC.bind(this);
    this.handleLogic = this.handleLogic.bind(this);

  }
//document.getElementById("snare1").addEventListener("keydown",this.handlePressQ())
  componentDidMount(){
    document.addEventListener("keydown",this.handleLogic);

  }

  handleLogic(event) {
    switch(event.key) {
        case "q":
      this.handlePressQ()
      break;
      case "w":
        this.handlePressW()
      default: 
      console.log(event.key + " was pressed")
    }
  }


  handlePressQ() {
    document.getElementById("Q").play()
    };

  handlePressW() {
    document.getElementById("W").play()
    };

  handlePressE() {
    document.getElementById("E").play()
    };

  handlePressA() {
    document.getElementById("A").play()
    };

  handlePressS() {
    document.getElementById("S").play()
    };

  handlePressD() {
    document.getElementById("D").play()
    };

  handlePressZ() {
    document.getElementById("Z").play()
    };

  handlePressX() {
    document.getElementById("X").play()
    };

  handlePressC() {
    document.getElementById("C").play()
    };

//Q, `W, E, A, S. D, Z, X, C.
  render(){
    return (
      <div id="drum-machine">
      <button id="snare1" className="drum-pad" onClick={this.handlePressQ}>Q
      <audio className="clip" id="Q" src="https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3"></audio>
      </button>
      <button id="snare2" className="drum-pad" onClick={this.handlePressW}>W
      <audio className="clip" id="W" src="https://s3.amazonaws.com/freecodecamp/drums/Heater-2.mp3"></audio>
      </button>
      <button id="snare3" className="drum-pad" onClick={this.handlePressE}>E
      <audio className="clip" id="E" src="https://s3.amazonaws.com/freecodecamp/drums/Heater-6.mp3"></audio>
      </button>
      <button id="Bass1" className="drum-pad" onClick={this.handlePressA}>A
      <audio className="clip" id="A" src="https://s3.amazonaws.com/freecodecamp/drums/punchy_kick_1.mp3"></audio>
      </button>
      <button id="Bass2" className="drum-pad" onClick={this.handlePressS}>S
      <audio className="clip" id="S" src="https://s3.amazonaws.com/freecodecamp/drums/side_stick_1.mp3"></audio>
      </button>
      <button id="Bass3" className="drum-pad" onClick={this.handlePressD}>D
      <audio className="clip" id="D" src="https://s3.amazonaws.com/freecodecamp/drums/Brk_Snr.mp3"></audio>
      </button>
      <button id="Kick1" className="drum-pad" onClick={this.handlePressZ}>Z
      <audio className="clip" id="Z" src="https://s3.amazonaws.com/freecodecamp/drums/Dsc_Oh.mp3"></audio>
      </button>
      <button id="Kick2" className="drum-pad" onClick={this.handlePressX}>X
      <audio className="clip" id="X" src="https://s3.amazonaws.com/freecodecamp/drums/Cev_H2.mp3"></audio>
      </button>
      <button id="Kick3" className="drum-pad" onClick={this.handlePressC}>C
      <audio className="clip" id="C" src="https://s3.amazonaws.com/freecodecamp/drums/RP4_KICK_1.mp3"></audio>
      </button>
      <div id="display"></div>
    </div>
    )
  }
}

export default DrumMachine;
