import React, { Component } from 'react';
import { Provider, connect } from 'react-redux';
import { createStore } from 'redux';
import './App.css';
import { assignmentExpression } from '@babel/types';

const play = 'play'

const playQ = () => {
  return{
  type: play,
  playback: "yes"
  }
}

const reducer = (state, action)=>{
  switch(action.type) {
    case play:
    return Object.assign({}, state, {playback: action.playback })
    default:
      return state

  }
}

const store = createStore(reducer)

class DrumMachine extends Component {
  constructor(props){
    super(props)
  }

  handlePress(){
     this.props.playQ()
  }


  render(){
    return(
        <div id="drum-machine">
          <button id="snare1" className="drum-pad"><audio id="Q"></audio></button>
          <button id="snare2" className="drum-pad"><audio id="W"></audio></button>
          <button id="snare3" className="drum-pad"><audio id="E"></audio></button>
          <button id="kick1" className="drum-pad"><audio id="A"></audio></button>
          <button id="kick2" className="drum-pad"><audio id="S"></audio></button>
          <button id="kick3" className="drum-pad"><audio id="D"></audio></button>
          <button id="clap1" className="drum-pad"><audio id="Z"></audio></button>
          <button id="clap2" className="drum-pad"><audio id="X"></audio></button>
          <button id="clap3" className="drum-pad"><audio id="C"></audio></button>
          <div id="display"></div>
        </div>
    )

  }
}



export default DrumMachine;
